// App.js
import React, { useState, useEffect } from "react";
import { useSocket } from "./socket/socket";

const App = () => {
  const {
    isConnected,
    messages,
    users,
    typingUsers,
    connect,
    disconnect,
    sendMessage,
    setTyping,
  } = useSocket();

  const [username, setUsername] = useState("");
  const [message, setMessage] = useState("");
  const [joined, setJoined] = useState(false);

  // Handle joining chat
  const handleJoin = () => {
    if (username.trim()) {
      connect(username);
      setJoined(true);
    }
  };

  // Handle sending a message
  const handleSend = (e) => {
    e.preventDefault();
    if (message.trim()) {
      sendMessage(message);
      setMessage("");
      setTyping(false);
    }
  };

  // Typing indicator
  const handleTyping = (e) => {
    setMessage(e.target.value);
    setTyping(e.target.value.length > 0);
  };

  useEffect(() => {
    return () => {
      disconnect();
    };
  }, [disconnect]);

  if (!joined) {
    return (
      <div style={styles.container}>
        <h2>Join the Chat</h2>
        <input
          type="text"
          placeholder="Enter your username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={styles.input}
        />
        <button onClick={handleJoin} style={styles.button}>
          Join
        </button>
      </div>
    );
  }

  return (
    <div style={styles.chatContainer}>
      <div style={styles.sidebar}>
        <h3>Online Users</h3>
        {users.map((user) => (
          <div key={user.id} style={styles.user}>
            ðŸŸ¢ {user.username}
          </div>
        ))}
        <button style={styles.leaveButton} onClick={disconnect}>
          Leave Chat
        </button>
      </div>

      <div style={styles.chatArea}>
        <div style={styles.messages}>
          {messages.map((msg) => (
            <div key={msg.id} style={msg.system ? styles.systemMsg : styles.msg}>
              <b>{msg.system ? "" : msg.sender + ": "}</b> {msg.message}
              <div style={styles.timestamp}>
                {new Date(msg.timestamp).toLocaleTimeString()}
              </div>
            </div>
          ))}
        </div>

        {typingUsers.length > 0 && (
          <div style={styles.typing}>
            {typingUsers.join(", ")} is typing...
          </div>
        )}

        <form onSubmit={handleSend} style={styles.form}>
          <input
            type="text"
            placeholder="Type your message..."
            value={message}
            onChange={handleTyping}
            style={styles.inputMsg}
          />
          <button type="submit" style={styles.button}>
            Send
          </button>
        </form>
      </div>
    </div>
  );
};

// Basic styles
const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    height: "100vh",
  },
  input: {
    padding: "10px",
    margin: "10px",
    width: "250px",
    borderRadius: "8px",
    border: "1px solid #ccc",
  },
  button: {
    padding: "10px 20px",
    backgroundColor: "#4caf50",
    color: "white",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
  },
  chatContainer: {
    display: "flex",
    height: "100vh",
  },
  sidebar: {
    width: "250px",
    backgroundColor: "#1e1e2f",
    color: "white",
    padding: "15px",
    overflowY: "auto",
  },
  chatArea: {
    flex: 1,
    display: "flex",
    flexDirection: "column",
    backgroundColor: "#f2f2f2",
    padding: "15px",
  },
  messages: {
    flex: 1,
    overflowY: "auto",
    marginBottom: "10px",
  },
  msg: {
    backgroundColor: "#fff",
    borderRadius: "8px",
    padding: "8px",
    margin: "5px 0",
  },
  systemMsg: {
    textAlign: "center",
    color: "#888",
    fontStyle: "italic",
    margin: "5px 0",
  },
  typing: {
    color: "#666",
    fontStyle: "italic",
    marginBottom: "5px",
  },
  form: {
    display: "flex",
  },
  inputMsg: {
    flex: 1,
    padding: "10px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    marginRight: "10px",
  },
  timestamp: {
    fontSize: "0.7em",
    color: "#888",
  },
  user: {
    margin: "5px 0",
  },
  leaveButton: {
    marginTop: "20px",
    backgroundColor: "#e74c3c",
    color: "white",
    border: "none",
    padding: "8px 10px",
    borderRadius: "8px",
    cursor: "pointer",
  },
};

export default App;
